import React, { useState } from 'react';
import './App.css';

function App() {
  const [bold, setBold] = useState([false, false, false, false]);
  const [italic, setItalic] = useState([false, false, false, false]);
  const [underline, setUnderline] = useState([false, false, false, false]);
  const [fontSizes, setFontSizes] = useState(['16px', '16px', '16px', '16px']);
  const [colors, setColors] = useState(['#000000', '#000000', '#000000', '#000000']);
  const sentences = [
    "He's not the sharpest knife in the drawer.",
    "The big building was blazing with lights.",
    "Now you must answer some big questions.",
    "Get Your Act Together!"
  ];

  const toggleBold = (index) => {
    const updatedBold = [...bold];
    updatedBold[index] = !updatedBold[index];
    setBold(updatedBold);
  };

  const toggleItalic = (index) => {
    const updatedItalic = [...italic];
    updatedItalic[index] = !updatedItalic[index];
    setItalic(updatedItalic);
  };

  const toggleUnderline = (index) => {
    const updatedUnderline = [...underline];
    updatedUnderline[index] = !updatedUnderline[index];
    setUnderline(updatedUnderline);
  };

  const changeFontSize = (index) => {
    const newSize = prompt('Enter font size (in px):', fontSizes[index]);
    if (newSize) {
      const updatedFontSizes = [...fontSizes];
      updatedFontSizes[index] = `${parseInt(newSize)}px`;
      setFontSizes(updatedFontSizes);
    }
  };

  const changeColor = (index) => {
    const newColor = prompt('Enter color:', colors[index]);
    if (newColor) {
      const updatedColors = [...colors];
      updatedColors[index] = newColor;
      setColors(updatedColors);
    }
  };

  return (
    <div className="App">
      <div className="grid-container">
        <div className="grid-column">
          {[0, 1, 2, 3].map((index) => (
            <div key={index}>
              <button onClick={() => toggleBold(index)}>Bold</button>
              <button onClick={() => toggleItalic(index)}>Italic</button>
              <button onClick={() => toggleUnderline(index)}>Underline</button>
              <button onClick={() => changeFontSize(index)}>Font-size</button>
              <button onClick={() => changeColor(index)}>Color</button>
            </div>
          ))}
        </div>
        <div className="grid-column">
          {sentences.map((sentence, index) => (
            <div key={index} className="grid-item sentence"
              style={{
                fontWeight: bold[index] ? 'bold' : 'normal',
                fontStyle: italic[index] ? 'italic' : 'normal',
                textDecoration: underline[index] ? 'underline' : 'none',
                fontSize: fontSizes[index],
                color: colors[index]
              }}
            >
              {sentence}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
