import React, { useState } from 'react';
import './styles.css'; // Import your CSS file

function TextEditor() {
  const [styles, setStyles] = useState([
    [{ fontWeight: 'normal' }, { fontWeight: 'normal' }],
    [{ fontWeight: 'normal' }, { fontWeight: 'normal' }],
    [{ fontWeight: 'normal' }, { fontWeight: 'normal' }],
    [{ fontWeight: 'normal' }, { fontWeight: 'normal' }],
  ]);

  const sentences = [
    "He's not the sharpest knife in the drawer.",
    "The big building was blazing with lights.",
    "Now you must answer some big questions.",
    "Get Your Act Together!",
  ];

  const handleStyleChange = (row, col, style) => {
    const updatedStyles = [...styles];
    updatedStyles[row][col] = { ...updatedStyles[row][col], ...style };
    setStyles(updatedStyles);
  };

  const handleTextChange = (row, text) => {
    const updatedSentences = [...sentences];
    updatedSentences[row] = text;
    setStyles((prevStyles) =>
      prevStyles.map((rowStyles, rowIndex) =>
        rowIndex === row ? rowStyles.map(() => ({ ...prevStyles[row][0] })) : rowStyles
      )
    );
  };

  return (
    <div className="grid-container">
      {styles.map((row, rowIndex) => (
        <div key={rowIndex} className="grid-row">
          <div className="text-editor">
            <button
              onClick={() => handleStyleChange(rowIndex, 0, { fontWeight: 'bold' })}
            >
              Bold
            </button>
            <button
              onClick={() => handleStyleChange(rowIndex, 0, { fontStyle: 'italic' })}
            >
              Italic
            </button>
            <button
              onClick={() => handleStyleChange(rowIndex, 0, { textDecoration: 'underline' })}
            >
              Underline
            </button>
            <input
              type="number"
              min={1}
              onChange={(e) => handleStyleChange(rowIndex, 0, { fontSize: e.target.value })}
            />
            <input
              type="color"
              onChange={(e) => handleStyleChange(rowIndex, 0, { color: e.target.value })}
            />
          </div>
          <div className="text-box" style={styles[rowIndex][0]}>
            <input
              type="text"
              value={sentences[rowIndex]}
              onChange={(e) => handleTextChange(rowIndex, e.target.value)}
            />
          </div>
        </div>
      ))}
    </div>
  );
}

export default TextEditor;
