import React, { useState } from "react";
import "./style.css";

const TextEditor = () => {
  const [text, setText] = useState(""); // State to store the text content
  const [fontSize, setFontSize] = useState("16px"); // State to store font size
  const [color, setColor] = useState("black"); // State to store color
  const [isBold, setIsBold] = useState(false); // State to toggle bold
  const [isItalic, setIsItalic] = useState(false); // State to toggle italic
  const [isUnderline, setIsUnderline] = useState(false); // State to toggle underline

  // Function to handle text change
  const handleChange = (e) => {
    setText(e.target.value);
  };

  // Function to handle bold button click
  const handleBoldClick = () => {
    setIsBold(!isBold);
  };

  // Function to handle italic button click
  const handleItalicClick = () => {
    setIsItalic(!isItalic);
  };

  // Function to handle underline button click
  const handleUnderlineClick = () => {
    setIsUnderline(!isUnderline);
  };

  // Function to handle font size change
  const handleFontSizeChange = (e) => {
    setFontSize(e.target.value + "px");
  };

  // Function to handle color change
  const handleColorChange = (e) => {
    setColor(e.target.value);
  };

  return (
    <div className="text-editor">
      <div>
        <button onClick={handleBoldClick}>Bold</button>
        <button onClick={handleItalicClick}>Italic</button>
        <button onClick={handleUnderlineClick}>Underline</button>
        <input
          type="number"
          value={parseInt(fontSize)}
          onChange={handleFontSizeChange}
        />
        <input type="color" value={color} onChange={handleColorChange} />
      </div>
      <div className="text-box"
        style={{
          fontWeight: isBold ? "bold" : "normal",
          fontStyle: isItalic ? "italic" : "normal",
          textDecoration: isUnderline ? "underline" : "none",
          fontSize: fontSize,
          color: color
        }}
      >
        <textarea value={text} onChange={handleChange} />
      </div>
    </div>
  );
};

export default TextEditor;
