import React, { useState } from 'react';
import '../../styles/style.css';

function TextEditor() {
  const [styles, setStyles] = useState([
    [{ fontWeight: 'normal' }, { fontStyle: 'normal' }, { textDecoration: 'none' }, { fontSize: '16px' }, { color: '#000000' }],
    [{ fontWeight: 'normal' }, { fontStyle: 'normal' }, { textDecoration: 'none' }, { fontSize: '16px' }, { color: '#000000' }],
    [{ fontWeight: 'normal' }, { fontStyle: 'normal' }, { textDecoration: 'none' }, { fontSize: '16px' }, { color: '#000000' }],
    [{ fontWeight: 'normal' }, { fontStyle: 'normal' }, { textDecoration: 'none' }, { fontSize: '16px' }, { color: '#000000' }],
  ]);

  useEffect(() => {
    console.log('Styles updated:', styles);
  }, [styles]);

  const sentences = [
    "He's not the sharpest knife in the drawer.",
    "The big building was blazing with lights.",
    "Now you must answer some big questions.",
    "Get Your Act Together!",
  ];

  const toggleStyle = (row, col, styleProperty) => {
    const currentStyle = styles[row][col];
    let updatedStyle;
    if (currentStyle[styleProperty]) {
      updatedStyle = { ...currentStyle, [styleProperty]: undefined };
    } else {
      updatedStyle = { ...currentStyle, [styleProperty]: styleProperty === 'fontSize' ? '16px' : styleProperty === 'color' ? '#000000' : 'bold' };
    }
    handleStyleChange(row, col, updatedStyle);
  };

  const handleStyleChange = (row, col, style) => {
    const updatedStyles = [...styles];
    updatedStyles[row][col] = { ...updatedStyles[row][col], ...style };
    setStyles(updatedStyles);
  };

  const handleTextChange = (row, text) => {
    const updatedSentences = [...sentences];
    updatedSentences[row] = text;
    setStyles((prevStyles) =>
      prevStyles.map((rowStyles, rowIndex) =>
        rowIndex === row ? rowStyles.map(() => ({ ...prevStyles[row][0] })) : rowStyles
      )
    );
  };

  return (
    <div className="grid-container">
      {styles.map((row, rowIndex) => (
        <div key={rowIndex} className="grid-row">
          <div className="text-editor">
            <button onClick={() => toggleStyle(rowIndex, 0, 'fontWeight')}>Bold</button>
            <button onClick={() => toggleStyle(rowIndex, 1, 'fontStyle')}>Italic</button>
            <button onClick={() => toggleStyle(rowIndex, 2, 'textDecoration')}>Underline</button>
            <button onClick={() => toggleStyle(rowIndex, 3, 'fontSize')}>Font Size</button>
            <input
              type="number"
              min={1}
              onChange={(e) => handleStyleChange(rowIndex, 3, { fontSize: e.target.value + 'px' })}
              value={parseInt(styles[rowIndex][3].fontSize)}
            />
            <button onClick={() => toggleStyle(rowIndex, 4, 'color')}>Color</button>
            <input
              type="color"
              onChange={(e) => handleStyleChange(rowIndex, 4, { color: e.target.value })}
              value={styles[rowIndex][4].color}
            />
          </div>
          <div className="text-box" style={styles[rowIndex][0]}>
            <input
              type="text"
              value={sentences[rowIndex]}
              onChange={(e) => handleTextChange(rowIndex, e.target.value)}
            />
          </div>
        </div>
      ))}
    </div>
  );
}

export default TextEditor;
