import React, { useState } from "react";
import "./App.css";

function App() {
  const [bold, setBold] = useState([false, false, false, false]);
  const [italic, setItalic] = useState([false, false, false, false]);
  const [underline, setUnderline] = useState([false, false, false, false]);
  const [fontSize, setFontSize] = useState([16, 16, 16, 16]);
  const [color, setColor] = useState([
    "#000000",
    "#000000",
    "#000000",
    "#000000",
  ]);

  const toggleBold = (index) => {
    const newBold = [...bold];
    newBold[index] = !newBold[index];
    setBold(newBold);
  };

  const toggleItalic = (index) => {
    const newItalic = [...italic];
    newItalic[index] = !newItalic[index];
    setItalic(newItalic);
  };

  const toggleUnderline = (index) => {
    const newUnderline = [...underline];
    newUnderline[index] = !newUnderline[index];
    setUnderline(newUnderline);
  };

  const changeFontSize = (index, e) => {
    const newFontSize = [...fontSize];
    newFontSize[index] = parseInt(e.target.value);
    setFontSize(newFontSize);
  };

  const changeColor = (index, e) => {
    const newColor = [...color];
    newColor[index] = e.target.value;
    setColor(newColor);
  };

  return (
    <div className="App">
      <div className="grid-container">
        {[0, 1, 2, 3].map((index) => (
          <div className="grid-item" key={index}>
            <div className="button-container">
              <button onClick={() => toggleBold(index)}>Bold</button>
              <button onClick={() => toggleItalic(index)}>Italic</button>
              <button onClick={() => toggleUnderline(index)}>Underline</button>
            </div>
            <input
              type="number"
              min="12"
              max="36"
              value={fontSize[index]}
              onChange={(e) => changeFontSize(index, e)}
            />
            <input
              type="color"
              value={color[index]}
              onChange={(e) => changeColor(index, e)}
            />
            <div
              className={`sentence ${bold[index] ? "bold" : ""} ${
                italic[index] ? "italic" : ""
              } ${underline[index] ? "underline" : ""}`}
              style={{ fontSize: `${fontSize[index]}px`, color: color[index] }}
            >
              {
                [
                  "He's not the sharpest knife in the drawer.",
                  "The big building was blazing with lights.",
                  "Now you must answer some big questions.",
                  "Get Your Act Together!",
                ][index]
              }
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
