import React, { useState } from "react";
import "./App.css";

function App() {
  const [bold, setBold] = useState(false);
  const [italic, setItalic] = useState(false);
  const [underline, setUnderline] = useState(false);
  const [fontSize, setFontSize] = useState(16);
  const [color, setColor] = useState("#000000");

  const toggleBold = () => setBold(!bold);
  const toggleItalic = () => setItalic(!italic);
  const toggleUnderline = () => setUnderline(!underline);
  const changeFontSize = (e) => setFontSize(parseInt(e.target.value));
  const changeColor = (e) => setColor(e.target.value);

  return (
    <div className="App">
      <div className="grid-container">
        <div className="grid-item">
          <button onClick={toggleBold}>Bold</button>
          <button onClick={toggleItalic}>Italic</button>
          <button onClick={toggleUnderline}>Underline</button>
          <input
            type="number"
            min="12"
            max="36"
            value={fontSize}
            onChange={changeFontSize}
          />
          <input type="color" value={color} onChange={changeColor} />
        </div>
        <div
          className={`grid-item sentence ${bold ? "bold" : ""} ${
            italic ? "italic" : ""
          } ${underline ? "underline" : ""}`}
          style={{ fontSize: `${fontSize}px`, color: color }}
        >
          He's not the sharpest knife in the drawer.
        </div>
        {/* Add more rows for additional sentences */}
      </div>
    </div>
  );
}

export default App;
