import React, { useState } from 'react';
import '../../styles/style.css';

function TextEditor() {
  const [bold, setBold] = useState(false);
  const [italic, setItalic] = useState(false);
  const [underline, setUnderline] = useState(false);
  const [fontSize, setFontSize] = useState(16);
  const [color, setColor] = useState('#000000');

  // Function to handle button clicks
  const handleBoldClick = () => setBold(!bold);
  const handleItalicClick = () => setItalic(!italic);
  const handleUnderlineClick = () => setUnderline(!underline);

  // Function to handle font size change
  const handleFontSizeChange = (e) => setFontSize(parseInt(e.target.value));

  // Function to handle color change
  const handleColorChange = (e) => setColor(e.target.value);

  return (
    <div className="grid-container">
      <div className="grid-row">
        <div className="text-editor">
          <button onClick={handleBoldClick}>{bold ? 'Unbold' : 'Bold'}</button>
          <button onClick={handleItalicClick}>{italic ? 'Unitalic' : 'Italic'}</button>
          <button onClick={handleUnderlineClick}>{underline ? 'Underline Off' : 'Underline On'}</button>
          <label htmlFor="fontSizeInput">Font Size:</label>
          <input
            id="fontSizeInput"
            type="number"
            min={1}
            value={fontSize}
            onChange={handleFontSizeChange}
          />
          <label htmlFor="colorInput">Color:</label>
          <input
            id="colorInput"
            type="color"
            value={color}
            onChange={handleColorChange}
          />
        </div>
        <div className="text-box" style={{ fontWeight: bold ? 'bold' : 'normal', fontStyle: italic ? 'italic' : 'normal', textDecoration: underline ? 'underline' : 'none', fontSize: `${fontSize}px`, color }}>
          Sample Text
        </div>
      </div>
    </div>
  );
}

export default TextEditor;
