import React, { useState } from 'react';
import '../../styles/style.css'

function TextEditor() {
  const [styles, setStyles] = useState([
    [{ fontWeight: 'normal' }, { fontWeight: 'normal' }],
    [{ fontWeight: 'normal' }, { fontWeight: 'normal' }],
    // Removed the extra empty row
  ]);

  const handleStyleChange = (row, col, style) => {
    const updatedStyles = [...styles];
    updatedStyles[row][col] = { ...updatedStyles[row][col], ...style };
    setStyles(updatedStyles);
  };

  return (
    <div className="grid-container">
      {styles.slice(0, 2).map((row, rowIndex) => (  // Adjust to display only 2 rows
        <div key={rowIndex} className="grid-row">
          {row.map((col, colIndex) => (
            <div key={colIndex} className="grid-item">
              <div className="text-editor">
                <button
                  onClick={() => handleStyleChange(rowIndex, colIndex, { fontWeight: 'bold' })}
                >
                  Bold
                </button>
                <button
                  onClick={() => handleStyleChange(rowIndex, colIndex, { fontStyle: 'italic' })}
                >
                  Italic
                </button>
                <button
                  onClick={() => handleStyleChange(rowIndex, colIndex, { textDecoration: 'underline' })}
                >
                  Underline
                </button>
                <input
                  type="number"
                  min={1} // Set minimum font size
                  onChange={(e) =>
                    handleStyleChange(rowIndex, colIndex, { fontSize: e.target.value })
                  }
                />
                <input
                  type="color"
                  onChange={(e) => handleStyleChange(rowIndex, colIndex, { color: e.target.value })}
                />
              </div>
              <div className="text-box" style={styles[rowIndex][colIndex]}>
                {rowIndex === 0
                  ? "Bold, Italic, Underline, font-size, color"
                  : "The big building was blazing with lights."}
              </div>
            </div>
          ))}
        </div>
      ))}
    </div>
  );
}

export default TextEditor;
