import React, { useState } from "react";
import "./App.css";

// Component for sentence
const Sentence = ({ text, style }) => {
  return <p style={style}>{text}</p>;
};

// Component for button
const Button = ({ label, onClick }) => {
  return <button onClick={onClick}>{label}</button>;
};

// Component for input
const Input = ({ type, value, onChange }) => {
  return <input type={type} value={value} onChange={onChange} />;
};

const App = () => {
  const [bold, setBold] = useState(false);
  const [italic, setItalic] = useState(false);
  const [underline, setUnderline] = useState(false);
  const [fontSize, setFontSize] = useState(16);
  const [color, setColor] = useState("#000000");

  // Functions to handle button clicks
  const handleBoldClick = () => setBold(!bold);
  const handleItalicClick = () => setItalic(!italic);
  const handleUnderlineClick = () => setUnderline(!underline);

  // Function to handle font size change
  const handleFontSizeChange = (e) => setFontSize(parseInt(e.target.value));

  // Function to handle color change
  const handleColorChange = (e) => setColor(e.target.value);

  // Define sentences
  const sentences = [
    "He's not the sharpest knife in the drawer.",
    "He's got a few screws loose.",
    "She's not the brightest crayon in the box.",
    "He's as sharp as a bowling ball.",
  ];

  return (
    <div className="container">
      <div className="row">
        <Button label="Bold" onClick={handleBoldClick} />
        <Button label="Italic" onClick={handleItalicClick} />
        <Button label="Underline" onClick={handleUnderlineClick} />
        <Input type="number" value={fontSize} onChange={handleFontSizeChange} />
        <Input type="color" value={color} onChange={handleColorChange} />
      </div>
      {sentences.map((sentence, index) => (
        <div className="row" key={index}>
          <Sentence
            text={sentence}
            style={{
              fontWeight: bold ? "bold" : "normal",
              fontStyle: italic ? "italic" : "normal",
              textDecoration: underline ? "underline" : "none",
              fontSize: `${fontSize}px`,
              color: color,
            }}
          />
        </div>
      ))}
    </div>
  );
};

export default App;
