import React, { useState } from "react";
import './';

const TextContainer = ({ text, id }) => {
  const [textStyle, setTextStyle] = useState({
    fontWeight: "normal",
    fontStyle: "normal",
    textDecoration: "none",
    fontSize: 16,
    color: "#000",
  });

  const makeBold = () => {
    setTextStyle({ ...textStyle, fontWeight: "bold" });
  };

  const makeItalic = () => {
    setTextStyle({ ...textStyle, fontStyle: "italic" });
  };

  const makeUnderline = () => {
    setTextStyle({ ...textStyle, textDecoration: "underline" });
  };

  const changeFontSize = (event) => {
    setTextStyle({ ...textStyle, fontSize: event.target.value });
  };

  const changeColor = (event) => {
    setTextStyle({ ...textStyle, color: event.target.value });
  };

  return (
    <div className="text-container">
      <p id={`text-${id}`} style={textStyle}>
        {text}
      </p>
      <div>
        <button onClick={makeBold}>Bold</button>
        <button onClick={makeItalic}>Italic</button>
        <button onClick={makeUnderline}>Underline</button>
        <input
          type="number"
          id={`font-size-${id}`}
          value={textStyle.fontSize}
          onChange={changeFontSize}
        />
        <input
          type="color"
          id={`color-${id}`}
          value={textStyle.color}
          onChange={changeColor}
        />
      </div>
    </div>
  );
};

export default TextContainer;
